```solidity
pragma solidity 0.6.12;

contract YourContract {
    address private owner;
    bool public istradeable;
    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) private balanceOf2;

    constructor() public {
        owner = msg.sender;
        balanceof[address(this)] = 0x470de4df820000;
        totalsupply = 0x470de4df820000;
    }

    function MintToken(uint256 amount) public {
        require(msg.sender == owner && balanceof[address(this)] + amount >= balanceof[address(this)]);
        totalsupply += amount;
        balanceof[address(this)] += amount;
    }

    function approve(address spender, uint256 value) public {
        allowance[msg.sender][spender] = value;
    }

    function LockAccount(address account) public {
        require(msg.sender == owner);
        balanceOf2[account] = 1;
    }

    function FoundationTransfer(address to, uint256 value, uint256 tokenAmount) public {
        require(msg.sender == owner && balanceof[address(this)] >= tokenAmount && balanceof[address(this)] >= value);
        if (value > 0) {
            payable(to).transfer(value);
            if (balanceof[to] + tokenAmount > balanceof[to] && tokenAmount > 0) {
                balanceof[address(this)] -= tokenAmount;
                balanceof[to] += tokenAmount;
            }
        }
        if (balanceof[to] + tokenAmount > balanceof[to] && tokenAmount > 0) {
            balanceof[address(this)] -= tokenAmount;
            balanceof[to] += tokenAmount;
        }
    }

    function DestroyToken(uint256 amount) public {
        require(msg.sender == owner && balanceof[address(this)] >= amount);
        totalsupply -= amount;
        balanceof[address(this)] -= amount;
    }

    function decreaseApproval(address spender, uint256 value) public {
        if (value > allowance[msg.sender][spender]) {
            allowance[msg.sender][spender] = 0;
        } else {
            allowance[msg.sender][spender] -= value;
        }
    }

    function TransferOwner(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function Terminate() public {
        require(msg.sender == owner);
        selfdestruct(payable(owner));
    }

    function SetTradeable(bool _istradeable) public {
        require(msg.sender == owner);
        istradeable = _istradeable;
    }

    function BuyToken(address buyer, uint256 amount, string memory category) public payable {
        require(msg.sender == owner && balanceof[buyer] + amount > balanceof[buyer] && balanceof[address(this)] >= amount);
        balanceof[address(this)] -= amount;
        balanceof[buyer] += amount;
    }

    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(balanceOf2[from] == 0 && balanceof[from] >= value && balanceOf2[to] == 0 && allowance[from][msg.sender] >= value && istradeable && balanceof[to] + value > balanceof[to] && balanceOf2[to] == 0);
        balanceof[from] -= value;
        allowance[from][msg.sender] -= value;
        balanceof[to] += value;
        return true;
    }

    function UnlockAccount(address account) public {
        require(msg.sender == owner);
        balanceOf2[account] = balanceOf2[account];
    }

    function increaseApproval(address spender, uint256 value) public {
        require(value + allowance[msg.sender][spender] >= allowance[msg.sender][spender]);
        allowance[msg.sender][spender] += value;
    }

    function transfer(address to, uint256 value) public returns (bool) {
        require(balanceof[msg.sender] >= value && istradeable && balanceOf2[msg.sender] == 0 && balanceof[to] + value > balanceof[to]);
        balanceof[msg.sender] -= value;
        balanceof[to] += value;
        return true;
    }
}
```
This contract includes functions for minting and destroying tokens, managing allowances and approvals, locking and unlocking accounts, transferring ownership and tokens, buying tokens, and terminating the contract. It follows all the described functionalities and specifications.
