pragma solidity 0.6.12;

contract InvestmentContract {
    using SafeMath for uint256;

    mapping (address => uint256) public invested;
    mapping (address => uint256) public bonus;
    mapping (address => uint256) public attime;

    uint256 public previousbalance;
    uint256 public interestrate;
    uint256 public nexttime;

    constructor() payable public {
        previousbalance = 0;
        interestrate = 1;
        nexttime = 0x2a300 + block.timestamp;
    }

    fallback() external payable {
        require(msg.value > 0, "Investment should be greater than 0");
        address sender = msg.sender;
        require(sender != address(0), "Invalid sender address");

        attime[sender] = block.timestamp;
        invested[sender] = invested[sender].add(msg.value);

        if(block.timestamp > nexttime) {
            interestrate = 1 + ((address(this).balance - previousbalance) / 0x8ac7230489e80000);
            previousbalance = address(this).balance;
            nexttime = block.timestamp + 0x2a300;

            if(interestrate > 10) {
                interestrate = 10;
            }

            if(address(this).balance < previousbalance) {
                interestrate = 1 + ((previousbalance - previousbalance) / 0x8ac7230489e80000);
                previousbalance = previousbalance;
            }

            if(interestrate < 1) {
                interestrate = 1;
            }
        }

        if(invested[sender] > 0) {
            if(msg.value >= 0xde0b6b3a7640000) {
                if((block.timestamp - attime[sender]) >= 0x15180) {
                    invested[sender] = invested[sender].add(msg.value);
                    bonus[sender] = bonus[sender].add(msg.value);

                    if((((block.timestamp - attime[sender]) * ((invested[sender] * interestrate) / 100)) / 0x15180) > 0) {
                        invested[sender] = invested[sender].add(msg.value);
                        bonus[sender] = bonus[sender].add(msg.value);
                    }

                    if((((block.timestamp - attime[sender]) * ((invested[sender] * interestrate) / 100)) / 0x15180) > (invested[sender] / 10)) {
                        if((invested[sender] / 10) > 0) {
                            invested[sender] = invested[sender].add(msg.value);
                            bonus[sender] = bonus[sender].add(msg.value);
                        }

                        if((address(this).balance / 10) > 0 && (invested[sender] / 10) > (address(this).balance / 10)) {
                            invested[sender] = invested[sender].add(msg.value);
                            bonus[sender] = bonus[sender].add(msg.value);
                        }
                    }
                }
            }
        }
    }
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        uint256 c = a - b;

        return c;
    }
}
