pragma solidity 0.6.12;

contract AdvancedWallet {

    mapping(address => uint256) public isowner;
    mapping(uint256 => mapping(address => uint256)) public confirmations;
    mapping(uint256 => uint256) private allowance1;
    address[] public owners;
    uint256 public required;
    uint256 public transactionids;

    function revokeConfirmation(uint256 _transactionid) external payable {
        require(isowner[msg.sender] > 0);
        require(confirmations[_transactionid][msg.sender] > 0);
        require(allowance1[_transactionid] == 0);
        confirmations[_transactionid][msg.sender] = confirmations[_transactionid][msg.sender];
    }

    function addOwner(address _owner) external payable {
        require(isowner[_owner] == 0);
        require(_owner > address(0));
        require(required > 0);
        require((1 + owners.length) <= 50);
        require(required <= (1 + owners.length));
        isowner[_owner] = 1;
        owners.push(_owner);
    }

    function changeRequirement(uint256 _required) external payable {
        require(_required > 0);
        require(owners.length <= 50);
        require(_required <= owners.length);
        required = _required;
    }

    function removeOwner(address _owner) external payable {
        require(isowner[_owner] > 0);
        isowner[_owner] = 0;
        for (uint256 i = 0; i < owners.length - 1; i++)
            if (owners[i] == _owner) {
                owners[i] = owners[owners.length - 1];
                break;
            }
        owners.pop();
    }

    function replaceOwner(address _owner, address _newowner) external payable {
        require(isowner[_owner] > 0);
        require(isowner[_newowner] == 0);
        for (uint256 i = 0; i < owners.length; i++)
            if (owners[i] == _owner) {
                owners[i] = _newowner;
                break;
            }
        isowner[_newowner] = 1;
        isowner[_owner] = 0;
    }

    function confirmTransaction(uint256 _transactionid) external payable {
        require(isowner[msg.sender] > 0);
        require(allowance1[_transactionid] > 0);
        require(confirmations[_transactionid][msg.sender] == 0);
        confirmations[_transactionid][msg.sender] = 1;
        if (msg.value == 0 && allowance1[_transactionid] > 0 && confirmations[_transactionid][msg.sender] == 0) {
            for (uint256 i = 0; i < owners.length; i++) {
                if (confirmations[_transactionid][owners[i]] > 0 && required == 2) {
                    allowance1[_transactionid] = 1;
                    payable(owners[i]).call{value: allowance1[_transactionid]}("");
                    break;
                }
            }
        }
    }
}
```

This contract code has been written in Solidity 0.6.12. It includes the state variables you mentioned as well as the five payable functions: `revokeConfirmation`, `addOwner`, `changeRequirement`, `removeOwner`, `replaceOwner`, and `confirmTransaction`.
