```solidity
pragma solidity 0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";

contract MyContract {
    using SafeMath for uint256;

    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    address public owner;
    string public version;
    bool public emergencyflag;
    uint256 public fundingstartblock;
    uint256 public fundingendblock;
    uint256 public finalblocknumber;
    uint256 public totalsupply;

    constructor() public payable {
        owner = msg.sender;
        version = "1.0";
        emergencyflag = false;
        fundingstartblock = block.number;
        finalblocknumber = 0x58107;
        if(msg.value == 0){
            owner = msg.sender;
            emergencyflag = false;
            fundingstartblock = block.number;
        }
        version = "0";
    }

    function emergencyToggle() public {
        require(msg.sender == owner, "Not authorized");
        emergencyflag = !emergencyflag;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= balanceof[_from]);
        require(_value <= allowance[_from][msg.sender]);
        balanceof[_from] = balanceof[_from].sub(_value);
        balanceof[_to] = balanceof[_to].add(_value);
        allowance[_from][msg.sender] = allowance[_from][msg.sender].sub(_value);
        return true;
    }

    function changeEndBlock(uint256 _endBlock) public {
        require(msg.sender == owner, "Not authorized");
        require(_endBlock > fundingstartblock, "End block must be after start block");
        fundingendblock = _endBlock;
    }

    function mint(address _to, uint256 _amount) public {
        require(msg.sender == owner, "Not authorized");
        require(!emergencyflag, "Emergency flag must be false");
        totalsupply = totalsupply.add(_amount);
        require(totalsupply <= 0x295be96e64066972000000, "Total supply limit exceeded");
        balanceof[_to] = balanceof[_to].add(_amount);
    }

    function drain() public {
        require(msg.sender == owner, "Not authorized");
        payable(0xeE9b66740EcF1a3e583e61B66C5b8563882b5d12).transfer(address(this).balance);
    }

    fallback() external payable {
        buyTokens();
    }

    function buyTokens() public payable {
        require(!emergencyflag, "Emergency flag must be false");
        require(msg.value >= 0x1c6bf526340000, "Insufficient value");
        require(block.number <= fundingendblock, "Funding period has ended");
        uint256 amount = msg.value.mul(700);
        if(block.number < fundingstartblock.add(0x295f1)) {
            amount = msg.value.mul(875);
        }
        totalsupply = totalsupply.add(amount);
        require(totalsupply <= 0x295be96e64066972000000, "Total supply limit exceeded");
        balanceof[msg.sender] = balanceof[msg.sender].add(amount);
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] = balanceof[msg.sender].sub(_value);
        balanceof[_to] = balanceof[_to].add(_value);
        return true;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner, "Not authorized");
        require(newOwner != address(0), "New owner cannot be zero address");
        owner = newOwner;
    }
}
```

