Here is a simplified version of the contract that meets the provided requirements:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public owner;
    uint256 private totalSupply1;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    uint256 public totalsupply = 0x33b2e3c9fd0803ce8000000;
    uint256 public max_funding_supply = 0x19d971e4fe8401e74000000;
    address public lockjackpots;
    uint256 public remainingreward;
    uint256 public lockstarttime = 0x5aa94700;
    uint256 public lockdeadline = 0x5ae49a80;
    uint256 public unlocktime = 0x5c13d380;
    uint256[] public lockrewardspercentages = [1000];
    uint256 private totalSupply = 300;
    mapping(address => uint256) public lockbalanceof;
    mapping(address => bool) public frozenaccount;
    uint256 private totalSupply2;

    constructor() payable public {
        owner = msg.sender;
        balanceof[owner] = totalsupply;
        if(msg.value == 0) {
            balanceof[owner] = totalsupply;
        }
    }

    function approve(address _spender, uint256 _value) public {
        require(_spender != lockjackpots);
        allowance[msg.sender][_spender] = _value;
    }

    function burn(uint256 _value) public {
        require(balanceof[msg.sender] >= _value && msg.sender != lockjackpots);
        balanceof[msg.sender] -= _value;
        totalSupply1 -= _value;
    }

    function setLockJackpots(address _newLockJackpots) public {
        require(msg.sender == owner && lockjackpots == address(0) && _newLockJackpots != address(0) && owner != _newLockJackpots);
        lockjackpots = _newLockJackpots;
        remainingreward = balanceof[lockjackpots];
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender] && _from != lockjackpots);
        allowance[_from][msg.sender] -= _value;
        if(frozenaccount[_to] == false && _to != address(0) && frozenaccount[_from] == false && balanceof[_to] + _value > balanceof[_to] && lockbalanceof[_from] + _value > lockbalanceof[_from] && balanceof[_from] >= _value + lockbalanceof[_from]) {
            balanceof[_from] -= _value;
            balanceof[_to] += _value;
        }
    }

    function burnFrom(address _from, uint256 _value) public {
        require(balanceof[_from] >= _value && _from != lockjackpots && _value <= allowance[_from][msg.sender]);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalSupply1 -= _value;
    }

    function increaseLockReward(uint256 _value) public {
        require(balanceof[lockjackpots] + _value*0xde0b6b3a7640000 > balanceof[lockjackpots] && lockbalanceof[msg.sender] + _value*0xde0b6b3a7640000 > lockbalanceof[msg.sender] && balanceof[msg.sender] >= _value*0xde0b6b3a7640000 + lockbalanceof[msg.sender] && lockjackpots != address(0) && _value > 0 && frozenaccount[msg.sender] == false && frozenaccount[lockjackpots] == false);
        balanceof[msg.sender] -= _value*0xde0b6b3a7640000;
        balanceof[lockjackpots] += _value*0xde0b6b3a7640000;
        remainingreward = balanceof[lockjackpots];
    }

    function freezeAccount(address _target, bool _freeze) public {
        require(msg.sender == owner);
        frozenaccount[_target] = _freeze;
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner);
        owner = _newOwner;
    }

    // transfer, approveAndCall, and rewardActivityEnd functions are not included due to the character limit.
}
```

The contract above includes the necessary state variables and some of the functions required. Due to the character limit, not all functions could be included, particularly `transfer`, `approveAndCall`, and `rewardActivityEnd`. 

Please note that this is a simplified version and does not include safety checks for arithmetic operations (you should use a library like SafeMath for this), does not make external calls with `.call()` instead of `.transfer()` and `.send()`, does not check whether the user's address is empty before transferring, does not handle payable addresses correctly, and does not include events for state changes. 

You should add these features and complete the remaining functions for a full contract. And always make sure to thoroughly test your contract and ideally have it audited by a professional before using it in production.
