Here's a solidity smart contract that fulfills the requirements mentioned:

```solidity
pragma solidity 0.6.12;

contract ContractName {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) private balanceOf2;
    address private owner;
    uint256 public totalSupply;
    bool public isTradeable;
    uint256 public tokensPerWei;

    constructor() public {
        owner = msg.sender;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function setTokensPerWei(uint256 _value) public {
        require(msg.sender == owner);
        tokensPerWei = _value;
    }

    function lockAccount(address _account) public {
        require(msg.sender == owner);
        balanceOf2[_account] = 1;
    }

    function foundationTransfer(address _to, uint256 _value, uint256 _tokenAmount) public {
        require(msg.sender == owner);
        require(balanceOf[address(this)] >= _tokenAmount);
        require(balanceOf[address(this)] >= _value);

        if (_value > 0) {
            payable(_to).transfer(_value);
            if (_tokenAmount > 0 && balanceOf[_to] + _tokenAmount > balanceOf[_to]) {
                balanceOf[address(this)] -= _tokenAmount;
                balanceOf[_to] += _tokenAmount;
            }
        }

        if (_tokenAmount > 0 && balanceOf[_to] + _tokenAmount > balanceOf[_to]) {
            balanceOf[address(this)] -= _tokenAmount;
            balanceOf[_to] += _tokenAmount;
        }
    }

    function buyToken(bytes32) public payable {
        require(msg.value > 0);
        uint256 amountToBuy = (tokensPerWei * 1e18 * msg.value) / 1e18;
        require(balanceOf[address(this)] + amountToBuy > balanceOf[address(this)]);
        totalSupply += amountToBuy;
        balanceOf[msg.sender] += amountToBuy;
    }

    function transferOwner(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function terminate() public {
        require(msg.sender == owner);
        selfdestruct(msg.sender);
    }

    function setTradeable(bool _value) public {
        require(msg.sender == owner);
        isTradeable = _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value > 0);
        require(isTradeable);
        require(balanceOf2[_from] == 0);
        require(balanceOf[_from] >= _value);
        require(balanceOf[_to] + _value > balanceOf[_to]);
        require(balanceOf2[_to] == 0);
        require(balanceOf2[address(this)] == 0);
        require(allowance[_from][msg.sender] >= _value);
        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function unlockAccount(address _account) public {
        require(msg.sender == owner);
        balanceOf2[_account] = balanceOf2[_account];
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceOf2[address(this)] == 0);
        require(balanceOf[msg.sender] >= _value);
        require(balanceOf[_to] + _value > balanceOf[_to]);
        require(_value > 0);
        require(isTradeable);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }
}
```

This contract includes all the functions mentioned in the description. Note that some functions require the caller to be the owner of the contract. The balance checks are done using the `balanceOf` mapping which keeps track of the balance of each address. From a security standpoint, the contract also checks for overflow and underflow where necessary. The `FoundationTransfer` function uses the `.transfer()` method instead of `.call()` or `.send()` to prevent reentrancy attacks.
