Here is a Solidity contract based on the provided descriptions and specifications:

```solidity
pragma solidity 0.6.12;

contract ICOContract {
    address public owner;
    uint256 private s_1;
    address public token;
    uint256 public starticopreico;
    uint256 public starticopreico2ndround;
    uint256 public starticomainsale;
    uint256 public endicopreico;
    uint256 public endicomainsale;
    uint256 public totalsoldtokens;
    uint256 public minpurchasepreico;
    uint256 public rateicowithbonusbydate;
    uint256 public unconfirmedsum;
    address payable public wallet;
    mapping(address => uint256) public contractbalanceof;
    mapping(address => uint256) public unconfirmedsumaddr;

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner, "Not contract owner");
        _;
    }

    function setStartIcoPreICO2ndRound(uint256 _value) public onlyOwner {
        require(_value > starticopreico && _value < endicopreico && block.timestamp < starticopreico);
        starticopreico2ndround = _value;
    }

    function unpause() external onlyOwner {
        (bool success,) = token.call(abi.encodeWithSignature("unpause()"));
        require(success, "Unpause failed");
    }

    function setStartIcoMainICO(uint256 _value) public onlyOwner {
        require(endicopreico < _value && _value < endicomainsale && block.timestamp < starticopreico);
        starticomainsale = _value;
    }

    function transferEthToMultisig() public onlyOwner {
        uint256 contractBalance = address(this).balance;
        if(block.timestamp < starticomainsale || (block.timestamp > endicomainsale && totalsoldtokens - unconfirmedsum >= s_1)) {
            (bool success,) = wallet.call{value: contractBalance}("");
            require(success, "Transfer failed");
        }
    }

    function SetPermissionsList(address _addr, uint8 _value) public onlyOwner {
        (bool success, bytes memory data) = token.call(abi.encodeWithSignature("GetPermissionsList(address)", _addr));
        require(success, "Call failed");
        uint8 v0 = abi.decode(data, (uint8));

        (success,) = token.call(abi.encodeWithSignature("SetPermissionsList(address,uint8)", _addr, _value));
        require(success, "SetPermissionsList failed");

        if(v0 != _value && _value == 0) {
            unconfirmedsum -= unconfirmedsumaddr[_addr];
            unconfirmedsumaddr[_addr] = 0;
        }
    }

    function pause() external onlyOwner {
        (bool success,) = token.call(abi.encodeWithSignature("pause()"));
        require(success, "Pause failed");
    }

    function GetPermissionsList(address _addr) public onlyOwner {
        (bool success,) = token.call(abi.encodeWithSignature("GetPermissionsList(address)", _addr));
        require(success, "GetPermissionsList failed");
    }

    function refund() public {
        require(contractbalanceof[msg.sender] > 0 && block.timestamp > endicomainsale && totalsoldtokens - unconfirmedsum < s_1);
        uint256 amountToRefund = contractbalanceof[msg.sender];
        contractbalanceof[msg.sender] = 0;
        (bool success,) = msg.sender.call{value: amountToRefund}("");
        require(success, "Refund failed");
    }

    function setStartIcoPreICO(uint256 _value) public onlyOwner {
        require(_value < endicopreico && block.timestamp < starticopreico);
        starticopreico = _value;
    }

    function setRateIcoMainSale(uint256 _value) public onlyOwner {
        rateicowithbonusbydate = _value;
    }

    function setEndIcoPreICO(uint256 _value) public onlyOwner {
        require(_value < starticomainsale && starticopreico < _value && block.timestamp < starticopreico);
        endicopreico = _value;
    }

    function setIcoDates(uint256 _starticopreico, uint256 _starticopreico2ndround, uint256 _endicopreico, uint256 _starticomainsale, uint256 _endicomainsale) public onlyOwner {
        require(block.timestamp < starticopreico && _starticomainsale < _endicomainsale && _endicopreico <= _starticomainsale && _starticopreico < _starticopreico2ndround && _starticopreico2ndround < _endicopreico);
        starticopreico = _starticopreico;
        starticopreico2ndround = _starticopreico2ndround;
        endicopreico = _endicopreico;
        starticomainsale = _starticomainsale;
        endicomainsale = _endicomainsale;
    }

    function setRateIcoPreICO(uint256 _value) public onlyOwner {
        rateicowithbonusbydate = _value;
    }

    function setEndIcoMainICO(uint256 _value) public onlyOwner {
        require(starticomainsale < _value && block.timestamp < starticopreico);
        endicomainsale = _value;
    }

    function transferOwnership(address _newOwner) public onlyOwner {
        require(_newOwner != address(0));
        owner = _newOwner;
    }

    function tokenBalanceOf(address _addr) public {
        (bool success,) = token.call(abi.encodeWithSignature("balanceOf(address)", _addr));
        require(success, "balanceOf failed");
    }

    function refundUnconfirmed() public {
        require(contractbalanceof[msg.sender] > 0 && block.timestamp > endicomainsale + 0x15180);
        (bool success, bytes memory data) = token.call(abi.encodeWithSignature("GetPermissionsList(address)", msg.sender));
        if(success && abi.decode(data, (uint8)) == 1) {
            uint256 amountToRefund = contractbalanceof[msg.sender];
            contractbalanceof[msg.sender] = 0;
            (success,) = msg.sender.call{value: amountToRefund}("");
            require(success, "Refund failed");
        } else {
            (success,) = token.call(abi.encodeWithSignature("burn(address,uint256)", msg.sender, unconfirmedsumaddr[msg.sender]));
            require(success, "Burn failed");
            unconfirmedsumaddr[msg.sender] = 0;
        }
    }
}
```

This contract implements the required functionality given in the description. Please note that the external contract `token` has to have the specific methods mentioned in the description for the calls to work (e.g., `unpause()`, `GetPermissionsList(address)`, `SetPermissionsList(address,uint8)`, `pause()`, `balanceOf(address)`, `burn(address,uint256)`).
