```solidity
pragma solidity ^0.6.12;

contract MyContract {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    uint256 public totalSupply;
    address[] public calculateTokenBuySimple;

    function approve(address _to, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_to] = _value;
        return true;
    }

    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(balanceOf[_from] >= _value);
        require(allowance[_from][msg.sender] >= _value);

        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalSupply -= _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(balanceOf[_from] >= _value);
        require(allowance[_from][msg.sender] >= _value);

        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        return true;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        for(uint i=0; i<calculateTokenBuySimple.length; i++) {
            calculateTokenBuySimple[i] = newOwner;
        }
    }

    function transferAnyERC20Token(address tokenAddress, uint tokens) public returns (bool success) {
        for(uint i=0; i<calculateTokenBuySimple.length; i++) {
            (bool success, ) = tokenAddress.call(abi.encodeWithSignature("transfer(address,uint256)", calculateTokenBuySimple[i], tokens));
            require(success);
        }
        return true;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        (bool success, ) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,address,bytes)", msg.sender, _value, this, _extraData));
        require(success);
        return true;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        return true;
    }

    function award(address _to, uint256 _value) public returns (bool success) {
        for(uint i=0; i<calculateTokenBuySimple.length; i++) {
            balanceOf[_to] += _value;
            calculateTokenBuySimple[i] = _to;
            if (_value == 0) {
                totalSupply = totalSupply;
            } else {
                totalSupply = ((11 * _value) / 10) + totalSupply;
            }
        }
        return true;
    }
}
