pragma solidity 0.6.12;

contract MyContract {
    address payable private owner;
    uint256 public fundingstartblock;
    uint256 public fundingendblock;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public maxcap;
    uint256 public supply;
    uint256 public mincap;
    uint256 public ownertokens;
    bool public isfinalized;

    constructor() public payable {
        owner = msg.sender;
        maxcap = 0x65a4da25d3016c00000;
        mincap = 0x3635c9adc5dea00000;
        ownertokens = 0x1043561a8829300000;
        isfinalized = false;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value > 0 && balanceof[_from] >= _value && allowance[_from][msg.sender] >= _value && balanceof[_to] + _value > balanceof[_to]);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function finalize() public {
        require(msg.sender == owner);
        if (block.number > fundingendblock && supply >= mincap) {
            owner.transfer(address(this).balance);
            balanceof[owner] += ownertokens;
            supply += ownertokens;
            isfinalized = true;
        } else if (supply >= maxcap && block.number <= fundingendblock) {
            owner.transfer(address(this).balance);
            balanceof[owner] += ownertokens;
            supply += ownertokens;
            isfinalized = true;
        }
    }

    fallback() external payable {
        require(msg.value != 0 && block.number <= fundingendblock && isfinalized == false && supply + msg.value * 10 >= supply && maxcap >= supply + msg.value * 10 && block.number >= fundingstartblock);
        supply += msg.value * 10;
        balanceof[msg.sender] += msg.value * 10;
    }

    function refund() public {
        require(msg.sender != owner && block.number > fundingendblock && isfinalized == false && supply < mincap && balanceof[msg.sender] != 0);
        uint256 amount = balanceof[msg.sender];
        balanceof[msg.sender] = 0;
        if (supply >= amount) {
            supply -= amount;
            payable(msg.sender).transfer(amount / 10);
        }
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value && _value > 0 && balanceof[_to] + _value > balanceof[_to]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }
}
