pragma solidity 0.6.12;

contract Crowdfunding {

    address payable public beneficiary;
    uint256 public fundingGoal;
    uint256 public amountRaised;
    uint256 public deadline;
    mapping(address => uint256) public balanceOf;
    bool private paused = false;
    bool private paused1 = false;

    constructor(address payable _beneficiary, uint256 _fundingGoal, uint256 _deadline) public {
        beneficiary = _beneficiary;
        fundingGoal = _fundingGoal;
        deadline = _deadline;
    }

    fallback() external payable {
        require(!paused, "Contract is paused");
        balanceOf[msg.sender] += msg.value;
        amountRaised += msg.value;
    }

    function checkGoalReached() public {
        require(block.timestamp >= deadline, "Deadline not reached");
        paused = true;
        if(amountRaised >= fundingGoal) {
            paused1 = true;
        }
    }

    function safeWithdrawal() public {
        require(block.timestamp >= deadline, "Deadline not reached");
        if(paused1 == false) {
            uint256 amount = balanceOf[msg.sender];
            balanceOf[msg.sender] = 0;
            if(amount > 0) {
                (bool success, ) = msg.sender.call{value: amount}("");
                if(!success) {
                    balanceOf[msg.sender] = amount;
                }
            }
        }
        if(msg.sender == beneficiary && paused1 != false) {
            (bool success, ) = beneficiary.call{value: amountRaised}("");
            if(!success) {
                paused1 = false;
            }
        }
    }
}
```

This contract represents a simple crowdfunding or fundraising campaign, which can be paused and resumed, and it also allows safe withdrawal of funds. The fallback function is used to accept incoming ETH and update the balance and amount raised. The `checkGoalReached` function is used to check if the funding goal has been reached after the deadline, and the `safeWithdrawal` function allows for withdrawal of funds.
