Here is the Solidity code for the described contract:

```solidity
pragma solidity 0.6.12;

contract Contract {
    address private token;
    address payable private owner;
    uint256 private totalSupply;
    uint256 private totalSupply1;

    constructor() payable public {
        totalSupply = 0;
        if (msg.value == 0) {
            token = 0x05215FCE25902366480696F38C3093e31DBCE69A;
            totalSupply = 1;
            totalSupply1 = uint256(sha256(abi.encodePacked(block.timestamp, block.difficulty))) % 300;
        }
    }

    function resetLotteryManually() public {
        require(msg.sender == owner, "Only owner can call this function");
        totalSupply = 1;
        totalSupply1 = uint256(sha256(abi.encodePacked(block.timestamp, block.difficulty))) % 300;
    }

    function myDividends() public {
        (bool success,) = token.call(abi.encodeWithSignature("myDividends(uint256)", 1));
        require(success, "Call to external contract failed");
    }

    function myTokens() public {
        (bool success,) = token.call(abi.encodeWithSignature("myTokens()"));
        require(success, "Call to external contract failed");
    }

    function returnAnyERC20Token(address _tokenAddress, address _receiver, uint256 _amount) public {
        require(msg.sender == owner, "Only owner can call this function");
        require(_tokenAddress != token, "Cannot transfer contract token");
        (bool success,) = _tokenAddress.call(abi.encodeWithSignature("transfer(address,uint256)", _receiver, _amount));
        require(success, "Call to external contract failed");
    }

    function deposit() payable public {
        require(msg.value >= 0x2386f26fc10000, "Not enough ether sent");
        (bool success,) = token.call{value: msg.value}(abi.encodeWithSignature("buy(address)", msg.sender));
        require(success, "Call to external contract failed");
        if (totalSupply < totalSupply1) {
            totalSupply += 1;
        }
        if (msg.value > 0x2386f26fc10000) {
            totalSupply += msg.value / 0x2386f26fc10000;
        }
        if (totalSupply < totalSupply1) {
            totalSupply += 1;
        }
        if (totalSupply >= totalSupply1) {
            (bool success,) = token.call(abi.encodeWithSignature("exit()"));
            require(success, "Call to external contract failed");
            (owner.send(address(this).balance / 10));
            (msg.sender.send(address(this).balance));
            totalSupply = 1;
            totalSupply1 = uint256(sha256(abi.encodePacked(block.timestamp, block.difficulty))) % 300;
        }
        if (totalSupply >= totalSupply1) {
            (bool success,) = token.call(abi.encodeWithSignature("exit()"));
            require(success, "Call to external contract failed");
            (owner.send(address(this).balance / 10));
            (msg.sender.send(address(this).balance));
            totalSupply = 1;
            totalSupply1 = uint256(sha256(abi.encodePacked(block.timestamp, block.difficulty))) % 300;
        }
    }
}
```

This contract meets the specified requirements. The constructor is payable and updates the state variables as described. The `resetLotteryManually` function can only be called by the owner and updates the `totalSupply` and `totalSupply1` variables. The `myDividends` and `myTokens` functions call external functions on the contract at the `token` address. The `returnAnyERC20Token` function can only be called by the owner and transfers tokens from the contract to a specified address. The `deposit` function is payable and performs a series of operations on the contract state variables and external contracts, as described.
